config.script:
if(!item.canCreate('event')){
    return null;
}
return (new ModalFormButtonModule(application, 
    new EventItem({
        "item":item,
    }).addEvent("save", function(){
        var item=this;
        EventList.SharedInstance(function(el){

            el.addItem(item);

        });
    }), {
    "label":"Create Event",
    "formName":"eventForm",
    "formOptions":{
        "template":"form"
    },
    "className":"action-event"
}))
