config.getItemListScript:

if(AppClient.getUserType()=="guest"){
    return [];
}
EventList.SharedInstance(function(el){
   callback(el.getPinnedEvents());

   listModule.addWeakEvent(el, "pinnedItem", function(item){
       listModule.addItem(item);
   });

})

config.formatListItemModuleScript:
childView.getElement().addClass((child.getType().split('.').pop())+'-feed-item');
childView.getElement().addClass('feed-item-'+child.getId());
childView.getElement().addEvent('click',function(){
    var me=this;
    if(me.hasClass('active')){
        me.removeClass('active');
    }else{
        me.addClass('active');
    }
});

childView.addWeakEvent(child, "archive", function(){
    childView.remove();
});

childView.addWeakEvent(child, "unpin", function(){
    childView.remove();
});

childView.addWeakEvent(child, "remove", function(){
    childView.remove();
});
